	DevaptMenubarClass.add_public_obj_property('items',				'',	null, false, false, []);
	DevaptMenubarClass.add_public_obj_property('items_resources',	'',	null, false, false, []);
	
	// MIXINS
	
	// BUILD
	DevaptMenubarClass.build_class();
	
	
	return DevaptMenubarClass;
